div.style:
width: 100%;
height: 100%;
background: red;
border-radius: 4px;
padding: 2px;
box-sizing: border-box;
display: flex;
align-items: center;

select.style:
width: 100%;
height: 100%;
font-size: 16px;
background: transparent;
color: white;
border: none;
outline: none;
appearance: none;
-webkit-appearance: none;
-moz-appearance: none;
cursor: pointer;

svg.script:
var select = document.getElementById("cardSelect");
var image  = document.getElementById("cardImage");

select.addEventListener("change", function() {
  image.setAttributeNS("http://www.w3.org/1999/xlink", "xlink:href", select.value);
});
